;   const numTolerance_f2c9_e6a8 = 0.9;   function checkAnswer_f2c9_e6a8() {     const inputEl = document.getElementById('num_input_f2c9_e6a8');     if (!inputEl) { return; }     const valStr = inputEl.value.trim();     const resultDiv = document.getElementById('result_f2c9_e6a8');     if (valStr === '') {       resultDiv.style.color = 'inherit';       resultDiv.textContent = 'Please enter a value.';       return;     }     const userVal = Number(valStr);     if (Number.isNaN(userVal)) {       resultDiv.style.color = 'inherit';       resultDiv.textContent = 'Please enter a valid number.';       return;     }     const lower = numAnswer_f2c9_e6a8 - numTolerance_f2c9_e6a8;     const upper = numAnswer_f2c9_e6a8 + numTolerance_f2c9_e6a8;     const isCorrect = (userVal &gt;= lower &amp;&amp; userVal &lt;= upper);     if (isCorrect) {       resultDiv.style.color = 'var(--qti-success-fg, #008000)';       resultDiv.textContent = 'CORRECT';     } else {       resultDiv.style.color = 'var(--qti-error-fg, #9b1b1b)';       if (userVal &gt; upper) {         resultDiv.style.color = 'var(--qti-error-fg, #9b1b1b)';         resultDiv.textContent = 'Too high. Try again.';       } else if (userVal &lt; lower) {         resultDiv.style.color = 'var(--qti-warning-fg, #b37100)';         resultDiv.textContent = 'Too low. Try again.';       } else {         resultDiv.textContent = 'Incorrect. Try again.';       }     }   }   document.addEventListener('DOMContentLoaded', function() {     const inputEl = document.getElementById('num_input_f2c9_e6a8');     if (!inputEl) return;     inputEl.addEventListener('keydown', function(e) {       if (e.key === 'Enter') { e.preventDefault(); checkAnswer_f2c9_e6a8(); }     });   });   &lt;/script&gt;    &lt;/div&gt;   &lt;/details&gt;  &lt;h2 id="so-output"&gt;&lt;Title&gt;. So output:&lt;/h2&gt; &lt;div id="dilution_factor_diluent_numeric-button-container" class="button-container"&gt; &lt;a class="md-button custom-button bb_text" href="bbq-dilution_fact